import React,  { Component} from 'react'
import {Link} from 'react-router-dom'
import Axios from 'axios'
import './Picture.css'
import 'font-awesome/css/font-awesome.css'


class Image extends Component {
    state = {
        picture: {}
    }

    componentDidMount() {
        const id = this.props.match.params.id
        Axios.get(`http://localhost:3001/albums/${id}`).then(resp => {
            this.setState({
                picture: resp.data

            }) 
        })

    }


    render(){
        return (
            <div className="picpage">
                <h1>{this.state.picture.name}</h1>
                <div className="picbox">
                    <div className= "arrowleft"><i className="fa fa-angle-left"></i></div>
                    <img src="https://img.memecdn.com/tada-tada-da-da-da-ant-man_c_2043617.jpg"/>
                    <div className="arrowright"><i className="fa fa-angle-right"></i></div>
                </div>
            </div>
        )
    }

}

export default Image
